import { useEffect, useState } from "react";
import axios from "axios";
import { Button } from "../components/ui/button";
import { Input } from "../components/ui/input";
import { Checkbox } from "../components/ui/checkbox";
import { Card, CardContent, CardHeader, CardTitle } from "../components/ui/card"; // Adjust your imports if you're not using a barrel file
import {
  CheckCircle2,
  Circle,
  Edit3,
  Trash2,
  Plus,
  LogOut,
  Save,
  X,
  ListTodo,
  User,
} from "lucide-react";

function TodoPage() {
  const [user, setUser] = useState(null);
  const [todos, setTodos] = useState([]);
  const [text, setText] = useState("");
  const [editId, setEditId] = useState(null);
  const [editText, setEditText] = useState("");

  useEffect(() => {
    axios
      .get("http://localhost:5000/auth/user", { withCredentials: true })
      .then((res) => setUser(res.data))
      .catch(() => (window.location.href = "/")); // redirect to login

    axios
      .get("http://localhost:5000/api/todos", { withCredentials: true })
      .then((res) => setTodos(res.data));
  }, []);

  const addTodo = async () => {
    if (!text.trim()) return;
    const res = await axios.post(
      "http://localhost:5000/api/todos",
      { text },
      { withCredentials: true }
    );
    setTodos([...todos, res.data]);
    setText("");
  };

  const startEdit = (todo) => {
    setEditId(todo._id);
    setEditText(todo.text);
  };

  const cancelEdit = () => {
    setEditId(null);
    setEditText("");
  };

  const saveEdit = async (id) => {
    if (!editText.trim()) return;
    const res = await axios.put(
      `http://localhost:5000/api/todos/${id}`,
      { text: editText },
      { withCredentials: true }
    );
    setTodos(todos.map((t) => (t._id === id ? res.data : t)));
    setEditId(null);
    setEditText("");
  };

  const toggleCompleted = async (todo) => {
    const res = await axios.put(
      `http://localhost:5000/api/todos/${todo._id}`,
      { completed: !todo.completed },
      { withCredentials: true }
    );
    setTodos(todos.map((t) => (t._id === todo._id ? res.data : t)));
  };

  const deleteTodo = async (id) => {
    await axios.delete(`http://localhost:5000/api/todos/${id}`, {
      withCredentials: true,
    });
    setTodos(todos.filter((t) => t._id !== id));
  };

  const logout = () => {
    axios
      .get("http://localhost:5000/auth/logout", { withCredentials: true })
      .then(() => {
        window.location.href = "/";
      });
  };

  if (!user) {
    return (
      <div className="min-h-screen flex items-center justify-center">
        <Card className="w-96">
          <CardContent className="flex items-center justify-center p-8">
            <div className="flex items-center space-x-2">
              <div className="animate-spin rounded-full h-6 w-6 border-b-2 border-blue-600"></div>
              <p className="text-lg font-medium text-gray-700">Loading...</p>
            </div>
          </CardContent>
        </Card>
      </div>
    );
  }

  const completedCount = todos.filter((todo) => todo.completed).length;
  const totalCount = todos.length;

}

export default TodoPage;
