import { useEffect, useState } from 'react';
import axios from 'axios';

function TodoPage() {
  const [user, setUser] = useState(null);
  const [todos, setTodos] = useState([]);
  const [text, setText] = useState('');
  const [editId, setEditId] = useState(null);
  const [editText, setEditText] = useState('');

  useEffect(() => {
    axios.get('http://localhost:5000/auth/user', { withCredentials: true })
      .then(res => setUser(res.data))
      .catch(() => window.location.href = '/');

    axios.get('http://localhost:5000/api/todos', { withCredentials: true })
      .then(res => setTodos(res.data));
  }, []);

  const addTodo = async () => {
    if (!text.trim()) return;
    const res = await axios.post('http://localhost:5000/api/todos', { text }, { withCredentials: true });
    setTodos([...todos, res.data]);
    setText('');
  };

  const startEdit = (todo) => {
    setEditId(todo._id);
    setEditText(todo.text);
  };

  const cancelEdit = () => {
    setEditId(null);
    setEditText('');
  };

  const saveEdit = async (id) => {
    if (!editText.trim()) return;
    const res = await axios.put(`http://localhost:5000/api/todos/${id}`, { text: editText }, { withCredentials: true });
    setTodos(todos.map(t => (t._id === id ? res.data : t)));
    setEditId(null);
    setEditText('');
  };

  const toggleCompleted = async (todo) => {
    const res = await axios.put(`http://localhost:5000/api/todos/${todo._id}`, { completed: !todo.completed }, { withCredentials: true });
    setTodos(todos.map(t => (t._id === todo._id ? res.data : t)));
  };

  const deleteTodo = async (id) => {
    await axios.delete(`http://localhost:5000/api/todos/${id}`, { withCredentials: true });
    setTodos(todos.filter(t => t._id !== id));
  };

  const logout = () => {
    axios.get('http://localhost:5000/auth/logout', { withCredentials: true })
      .then(() => window.location.href = '/');
  };

  if (!user) return <p>Loading...</p>;

  const completedCount = todos.filter(todo => todo.completed).length;
  const remainingCount = todos.length - completedCount;

  return (
    <div className="max-w-3xl mx-auto p-4">
      <div className="bg-white shadow-md rounded-2xl p-6 mb-6 flex justify-between items-center">
        <div>
          <h2 className="text-xl font-bold">Welcome back, {user.name}</h2>
          <p className="text-sm text-gray-500">{completedCount} of {todos.length} tasks completed</p>
        </div>
        <button onClick={logout} className="border px-4 py-2 rounded-lg hover:bg-gray-100">
          Logout
        </button>
      </div>

      <div className="grid grid-cols-3 gap-4 mb-6">
        <div className="bg-blue-500 text-white p-4 rounded-xl shadow-md text-center">
          <p>Total Tasks</p>
          <p className="text-2xl font-bold">{todos.length}</p>
        </div>
        <div className="bg-green-500 text-white p-4 rounded-xl shadow-md text-center">
          <p>Completed</p>
          <p className="text-2xl font-bold">{completedCount}</p>
        </div>
        <div className="bg-orange-500 text-white p-4 rounded-xl shadow-md text-center">
          <p>Remaining</p>
          <p className="text-2xl font-bold">{remainingCount}</p>
        </div>
      </div>

      <div className="bg-white shadow-md rounded-2xl p-4 mb-6">
        <h3 className="font-semibold mb-2">+ Add New Task</h3>
        <div className="flex gap-2">
          <input
            type="text"
            value={text}
            onChange={e => setText(e.target.value)}
            placeholder="What needs to be done?"
            className="w-full border rounded-lg p-2"
          />
          <button onClick={addTodo} className="bg-blue-400 text-white px-4 py-2 rounded-lg hover:bg-blue-500">
            + Add Task
          </button>
        </div>
      </div>

      <div className="bg-white shadow-md rounded-2xl p-4">
        <h3 className="font-semibold mb-2">Your Tasks</h3>
        {todos.map(todo => (
          <div key={todo._id} className={`flex items-center justify-between p-2 rounded-lg mb-2 ${todo.completed ? 'bg-green-100' : 'bg-gray-100'}`}>
            <div className="flex items-center gap-2">
              <input
                type="checkbox"
                checked={todo.completed}
                onChange={() => toggleCompleted(todo)}
              />
              {editId === todo._id ? (
                <input
                  type="text"
                  value={editText}
                  onChange={e => setEditText(e.target.value)}
                  className="border p-1 rounded"
                />
              ) : (
                <span className={`cursor-pointer ${todo.completed ? 'line-through text-gray-500' : ''}`}
                      onDoubleClick={() => startEdit(todo)}>
                  {todo.text}
                </span>
              )}
            </div>
            <div className="flex gap-2">
              {editId === todo._id ? (
                <>
                  <button onClick={() => saveEdit(todo._id)} className="text-sm px-2 py-1 bg-green-500 text-white rounded">Save</button>
                  <button onClick={cancelEdit} className="text-sm px-2 py-1 bg-gray-300 rounded">Cancel</button>
                </>
              ) : (
                <>
                  <button onClick={() => startEdit(todo)} className="text-sm px-2 py-1 bg-yellow-400 rounded">Edit</button>
                  <button onClick={() => deleteTodo(todo._id)} className="text-sm px-2 py-1 bg-red-500 text-white rounded">Delete</button>
                </>
              )}
            </div>
          </div>
        ))}
      </div>
    </div>
  );
}

export default TodoPage;
