"use client"

import { useEffect, useState } from "react"
import { Button } from "@/components/ui/button"
import { Input } from "@/components/ui/input"
import { Checkbox } from "@/components/ui/checkbox"
import { Card, CardContent, CardHeader, CardTitle } from "@/components/ui/card"
import { Badge } from "@/components/ui/badge"
import { Separator } from "@/components/ui/separator"
import { Avatar, AvatarFallback } from "@/components/ui/avatar"
import { CheckCircle2, Circle, Edit3, Trash2, Plus, LogOut, Save, X, ListTodo, User } from "lucide-react"

// Mock axios for preview environment
const mockAxios = {
  get: (url: string, config?: any) => {
    return new Promise((resolve, reject) => {
      setTimeout(() => {
        if (url.includes("/auth/user")) {
          resolve({ data: { name: "John Doe", id: "1" } })
        } else if (url.includes("/api/todos")) {
          const savedTodos = localStorage.getItem("todos")
          const todos = savedTodos
            ? JSON.parse(savedTodos)
            : [
                { _id: "1", text: "Complete project proposal", completed: false },
                { _id: "2", text: "Review team feedback", completed: true },
                { _id: "3", text: "Schedule client meeting", completed: false },
              ]
          resolve({ data: todos })
        } else if (url.includes("/auth/logout")) {
          resolve({ data: { success: true } })
        }
      }, 300)
    })
  },
  post: (url: string, data: any, config?: any) => {
    return new Promise((resolve) => {
      setTimeout(() => {
        if (url.includes("/api/todos")) {
          const newTodo = {
            _id: Date.now().toString(),
            text: data.text,
            completed: false,
          }
          const savedTodos = localStorage.getItem("todos")
          const todos = savedTodos ? JSON.parse(savedTodos) : []
          todos.push(newTodo)
          localStorage.setItem("todos", JSON.stringify(todos))
          resolve({ data: newTodo })
        }
      }, 200)
    })
  },
  put: (url: string, data: any, config?: any) => {
    return new Promise((resolve) => {
      setTimeout(() => {
        const savedTodos = localStorage.getItem("todos")
        const todos = savedTodos ? JSON.parse(savedTodos) : []
        const todoId = url.split("/").pop()
        const todoIndex = todos.findIndex((t: any) => t._id === todoId)

        if (todoIndex !== -1) {
          todos[todoIndex] = { ...todos[todoIndex], ...data }
          localStorage.setItem("todos", JSON.stringify(todos))
          resolve({ data: todos[todoIndex] })
        }
      }, 200)
    })
  },
  delete: (url: string, config?: any) => {
    return new Promise((resolve) => {
      setTimeout(() => {
        const savedTodos = localStorage.getItem("todos")
        const todos = savedTodos ? JSON.parse(savedTodos) : []
        const todoId = url.split("/").pop()
        const filteredTodos = todos.filter((t: any) => t._id !== todoId)
        localStorage.setItem("todos", JSON.stringify(filteredTodos))
        resolve({ data: { success: true } })
      }, 200)
    })
  },
}

// Use mock axios instead of real axios
const axios = mockAxios

function TodoPage() {
  const [user, setUser] = useState(null)
  const [todos, setTodos] = useState([])
  const [text, setText] = useState("")
  const [editId, setEditId] = useState(null)
  const [editText, setEditText] = useState("")

  useEffect(() => {
    axios
      .get("http://localhost:5000/auth/user", { withCredentials: true })
      .then((res) => setUser(res.data))
      .catch(() => (window.location.href = "/"))

    axios.get("http://localhost:5000/api/todos", { withCredentials: true }).then((res) => setTodos(res.data))
  }, [])

  const addTodo = async () => {
    if (!text.trim()) return
    const res = await axios.post("http://localhost:5000/api/todos", { text }, { withCredentials: true })
    setTodos([...todos, res.data])
    setText("")
  }

  const startEdit = (todo) => {
    setEditId(todo._id)
    setEditText(todo.text)
  }

  const cancelEdit = () => {
    setEditId(null)
    setEditText("")
  }

  const saveEdit = async (id) => {
    if (!editText.trim()) return
    const res = await axios.put(`http://localhost:5000/api/todos/${id}`, { text: editText }, { withCredentials: true })
    setTodos(todos.map((t) => (t._id === id ? res.data : t)))
    setEditId(null)
    setEditText("")
  }

  const toggleCompleted = async (todo) => {
    const res = await axios.put(
      `http://localhost:5000/api/todos/${todo._id}`,
      { completed: !todo.completed },
      { withCredentials: true },
    )
    setTodos(todos.map((t) => (t._id === todo._id ? res.data : t)))
  }

  const deleteTodo = async (id) => {
    await axios.delete(`http://localhost:5000/api/todos/${id}`, { withCredentials: true })
    setTodos(todos.filter((t) => t._id !== id))
  }

  const logout = () => {
    axios.get("http://localhost:5000/auth/logout", { withCredentials: true }).then(() => {
      localStorage.removeItem("todos")
      window.location.reload()
    })
  }

  if (!user) {
    return (
      <div className="min-h-screen bg-gradient-to-br from-blue-50 to-indigo-100 flex items-center justify-center">
        <Card className="w-96">
          <CardContent className="flex items-center justify-center p-8">
            <div className="flex items-center space-x-2">
              <div className="animate-spin rounded-full h-6 w-6 border-b-2 border-blue-600"></div>
              <p className="text-lg font-medium text-gray-700">Loading...</p>
            </div>
          </CardContent>
        </Card>
      </div>
    )
  }

  const completedCount = todos.filter((todo) => todo.completed).length
  const totalCount = todos.length

  return (
    <div className="min-h-screen bg-gradient-to-br from-blue-50 to-indigo-100">
      <div className="container mx-auto px-4 py-8 max-w-4xl">
        {/* Header */}
        <Card className="mb-8 shadow-lg border-0 bg-white/80 backdrop-blur-sm">
          <CardHeader>
            <div className="flex items-center justify-between">
              <div className="flex items-center space-x-4">
                <Avatar className="h-12 w-12 bg-gradient-to-r from-blue-500 to-purple-600">
                  <AvatarFallback className="text-white font-semibold">
                    {user.name?.charAt(0)?.toUpperCase() || <User className="h-6 w-6" />}
                  </AvatarFallback>
                </Avatar>
                <div>
                  <CardTitle className="text-2xl font-bold text-gray-800">Welcome back, {user.name}</CardTitle>
                  <p className="text-gray-600 mt-1">
                    {totalCount > 0 ? `${completedCount} of ${totalCount} tasks completed` : "No tasks yet"}
                  </p>
                </div>
              </div>
              <Button
                onClick={logout}
                variant="outline"
                className="flex items-center space-x-2 hover:bg-red-50 hover:border-red-200 hover:text-red-600"
              >
                <LogOut className="h-4 w-4" />
                <span>Logout</span>
              </Button>
            </div>
          </CardHeader>
        </Card>

        {/* Stats */}
        {totalCount > 0 && (
          <div className="grid grid-cols-1 md:grid-cols-3 gap-4 mb-8">
            <Card className="bg-gradient-to-r from-blue-500 to-blue-600 text-white border-0">
              <CardContent className="p-6">
                <div className="flex items-center justify-between">
                  <div>
                    <p className="text-blue-100">Total Tasks</p>
                    <p className="text-3xl font-bold">{totalCount}</p>
                  </div>
                  <ListTodo className="h-8 w-8 text-blue-200" />
                </div>
              </CardContent>
            </Card>
            <Card className="bg-gradient-to-r from-green-500 to-green-600 text-white border-0">
              <CardContent className="p-6">
                <div className="flex items-center justify-between">
                  <div>
                    <p className="text-green-100">Completed</p>
                    <p className="text-3xl font-bold">{completedCount}</p>
                  </div>
                  <CheckCircle2 className="h-8 w-8 text-green-200" />
                </div>
              </CardContent>
            </Card>
            <Card className="bg-gradient-to-r from-orange-500 to-orange-600 text-white border-0">
              <CardContent className="p-6">
                <div className="flex items-center justify-between">
                  <div>
                    <p className="text-orange-100">Remaining</p>
                    <p className="text-3xl font-bold">{totalCount - completedCount}</p>
                  </div>
                  <Circle className="h-8 w-8 text-orange-200" />
                </div>
              </CardContent>
            </Card>
          </div>
        )}

        {/* Add Todo */}
        <Card className="mb-8 shadow-lg border-0 bg-white/80 backdrop-blur-sm">
          <CardHeader>
            <CardTitle className="flex items-center space-x-2 text-xl">
              <Plus className="h-5 w-5 text-blue-600" />
              <span>Add New Task</span>
            </CardTitle>
          </CardHeader>
          <CardContent>
            <div className="flex space-x-3">
              <Input
                type="text"
                value={text}
                onChange={(e) => setText(e.target.value)}
                placeholder="What needs to be done?"
                className="flex-1 h-12 text-lg border-gray-200 focus:border-blue-500 focus:ring-blue-500"
                onKeyPress={(e) => e.key === "Enter" && addTodo()}
              />
              <Button
                onClick={addTodo}
                className="h-12 px-8 bg-gradient-to-r from-blue-500 to-blue-600 hover:from-blue-600 hover:to-blue-700"
                disabled={!text.trim()}
              >
                <Plus className="h-4 w-4 mr-2" />
                Add Task
              </Button>
            </div>
          </CardContent>
        </Card>

        {/* Todo List */}
        <Card className="shadow-lg border-0 bg-white/80 backdrop-blur-sm">
          <CardHeader>
            <CardTitle className="flex items-center justify-between">
              <span className="text-xl">Your Tasks</span>
              {totalCount > 0 && (
                <Badge variant="secondary" className="text-sm">
                  {totalCount} {totalCount === 1 ? "task" : "tasks"}
                </Badge>
              )}
            </CardTitle>
          </CardHeader>
          <CardContent>
            {todos.length === 0 ? (
              <div className="text-center py-12">
                <ListTodo className="h-16 w-16 text-gray-300 mx-auto mb-4" />
                <p className="text-xl font-medium text-gray-500 mb-2">No tasks yet</p>
                <p className="text-gray-400">Add your first task above to get started!</p>
              </div>
            ) : (
              <div className="space-y-3">
                {todos.map((todo, index) => (
                  <div key={todo._id}>
                    <div
                      className={`flex items-center space-x-4 p-4 rounded-lg transition-all duration-200 ${
                        todo.completed
                          ? "bg-green-50 border border-green-200"
                          : "bg-gray-50 hover:bg-gray-100 border border-gray-200"
                      }`}
                    >
                      <Checkbox
                        checked={todo.completed}
                        onCheckedChange={() => toggleCompleted(todo)}
                        className="h-5 w-5"
                      />

                      {editId === todo._id ? (
                        <div className="flex-1 flex items-center space-x-3">
                          <Input
                            type="text"
                            value={editText}
                            onChange={(e) => setEditText(e.target.value)}
                            className="flex-1 h-10"
                            onKeyPress={(e) => e.key === "Enter" && saveEdit(todo._id)}
                            autoFocus
                          />
                          <Button
                            onClick={() => saveEdit(todo._id)}
                            size="sm"
                            className="bg-green-600 hover:bg-green-700"
                          >
                            <Save className="h-4 w-4" />
                          </Button>
                          <Button onClick={cancelEdit} size="sm" variant="outline" className="hover:bg-gray-100">
                            <X className="h-4 w-4" />
                          </Button>
                        </div>
                      ) : (
                        <>
                          <span
                            className={`flex-1 text-lg cursor-pointer transition-all duration-200 ${
                              todo.completed ? "line-through text-gray-500" : "text-gray-800 hover:text-blue-600"
                            }`}
                            onDoubleClick={() => startEdit(todo)}
                            title="Double-click to edit"
                          >
                            {todo.text}
                          </span>
                          <div className="flex items-center space-x-2">
                            <Button
                              onClick={() => startEdit(todo)}
                              size="sm"
                              variant="outline"
                              className="hover:bg-blue-50 hover:border-blue-200 hover:text-blue-600"
                            >
                              <Edit3 className="h-4 w-4" />
                            </Button>
                            <Button
                              onClick={() => deleteTodo(todo._id)}
                              size="sm"
                              variant="outline"
                              className="hover:bg-red-50 hover:border-red-200 hover:text-red-600"
                            >
                              <Trash2 className="h-4 w-4" />
                            </Button>
                          </div>
                        </>
                      )}
                    </div>
                    {index < todos.length - 1 && <Separator className="my-2" />}
                  </div>
                ))}
              </div>
            )}
          </CardContent>
        </Card>
      </div>
    </div>
  )
}

export default TodoPage
